import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [name1, setName1] = useState('');
    const [name2, setName2] = useState('');
    const [result, setResult] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setResult('');

        try {
            const res = await axios.post('http://localhost:5000/flames', { name1, name2 });
            setResult(res.data.result);
        } catch (err) {
            setError('Failed to calculate result. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>FLAMES Game</h1>
            <p style={styles.subtitle}>Find your relationship status!</p>

            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.inputGroup}>
                    <input
                        type="text"
                        value={name1}
                        onChange={(e) => setName1(e.target.value)}
                        placeholder="Enter first name"
                        style={styles.input}
                        required
                    />
                </div>
                <div style={styles.inputGroup}>
                    <input
                        type="text"
                        value={name2}
                        onChange={(e) => setName2(e.target.value)}
                        placeholder="Enter second name"
                        style={styles.input}
                        required
                    />
                </div>
                <button type="submit" style={styles.button} disabled={loading}>
                    {loading ? 'Calculating...' : 'Calculate Relationship'}
                </button>
            </form>

            {error && <p style={styles.error}>{error}</p>}
            {result && (
                <div style={styles.result}>
                    <h2>Result:</h2>
                    <p style={styles.resultText}>{result}</p>
                </div>
            )}
        </div>
    );
}

const styles = {
    container: {
        maxWidth: '600px',
        margin: '0 auto',
        padding: '2rem',
        textAlign: 'center',
        fontFamily: 'Arial, sans-serif',
    },
    title: {
        color: '#2c3e50',
        fontSize: '2.5rem',
        marginBottom: '0.5rem',
    },
    subtitle: {
        color: '#7f8c8d',
        marginBottom: '2rem',
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '1rem',
    },
    inputGroup: {
        width: '100%',
    },
    input: {
        width: '100%',
        padding: '0.8rem',
        fontSize: '1rem',
        border: '2px solid #ddd',
        borderRadius: '8px',
        outline: 'none',
        transition: 'border-color 0.3s',
    },
    button: {
        backgroundColor: '#3498db',
        color: 'white',
        padding: '1rem',
        border: 'none',
        borderRadius: '8px',
        fontSize: '1.1rem',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
    },
    error: {
        color: '#e74c3c',
        marginTop: '1rem',
    },
    result: {
        marginTop: '2rem',
        padding: '1rem',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px',
    },
    resultText: {
        fontSize: '1.5rem',
        color: '#2c3e50',
        margin: '0.5rem 0',
    },
};


export default App;
